import React from 'react';
import { Link } from 'react-router-dom';
import { getUser } from '../services/userAPI';
import Loading from './Loading';
import icon from '../imagens/icon.png';

class Header extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      loading: false,
    };
  }

  componentDidMount() {
    this.getName();
  }

  async getName() {
    this.setState({ loading: true });
    const requisição = await getUser();
    this.setState({
      name: requisição.name,
      loading: false,
    });
  }

  render() {
    const { name, loading } = this.state;
    if (loading) {
      return <Loading />;
    }
    return (
      <header data-testid="header-component">
        <div className="user">
          <img src={ icon } width="35px" alt={ name } />
          <h4 data-testid="header-user-name">{ name }</h4>
        </div>
        <div
          className="navbar navbar-expand-lg navbar-light bg-light
        justify-content-center"
        >
          <nav className="navbar">
            <ul className="nav nav-pills nav-fill">
              <li className="nav-item">
                <a className="nav-link active" href="#">Ativo</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#">Link nav muito mais longo</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="#">Link</a>
              </li>
              <li className="nav-item">
                <a className="nav-link disabled" href="#">Desativado</a>
              </li>
            </ul>
            <Link
              className="nav-link active"
              to="/search"
              data-testid="link-to-search"
            >
              Search
            </Link>
            <Link
              className="nav-link active"
              to="/favorites"
              data-testid="link-to-favorites"
            >
              Favorites
            </Link>
            <Link
              className="nav-link active"
              to="/profile"
              data-testid="link-to-profile"
            >
              Profile
            </Link>
          </nav>
        </div>
      </header>
    );
  }
}

export default Header;
