import React from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import Header from '../component/Header';
import MusicCard from '../component/musicCard';
import Loading from '../component/Loading';
import musicsAPI from '../services/musicsAPI';

class Album extends React.Component {
  constructor() {
    super();
    this.state = {
      loading: false,
      artist: '',
      album: '',
      musics: [],

    };
  }

  componentDidMount() {
    this.getMusics();
  }

  getMusics = async () => {
    const { match: { params: { id } } } = this.props;
    this.setState({ loading: true });
    const musics = await musicsAPI(id);
    this.setState({
      loading: false,
      musics: musics.slice(1),
      artist: musics[0].artistName,
      album: musics[0].collectionName });
  }

  render() {
    const { loading, musics, album, artist } = this.state;
    const loadingElement = <Loading />;
    const link = (
      <div>
        <Link to="/search" data-testid="link-to-search">Home</Link>
        <h2 data-testid="artist-name">{artist}</h2>
        <h3 data-testid="album-name">{album}</h3>
        { musics.map((element) => (<MusicCard
          key={ element.trackId }
          music={ element }
        />))}
      </div>
    );
    return (
      <div data-testid="page-album">
        <Header />
        {loading ? loadingElement : link}
      </div>
    );
  }
}

Album.propTypes = {
  match: PropTypes.shape({
    params: PropTypes.shape({
      id: PropTypes.string,
    }),
  }).isRequired,
};

export default Album;
