import React from 'react';
import { createUser } from '../services/userAPI';

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      disabled: true,
    };
  }

  componentDidMount() {
    createUser();
  }

  handleChange = ({ target }) => {
    const { value } = target;
    const nb = 3;
    if (value.length >= nb) {
      this.setState({
        name: value,
        disabled: false,
      });
    } else {
      this.setState({
        disabled: true,
      });
    }
  }

  render() {
    const { value, name, disabled } = this.state;
    return (
      <div className="container d-flex justify-content-center" data-testid="page-login">
        <form>
          <div className="form-group">
            <label htmlFor="name">
              Nome
              <input
                data-testid="login-name-input"
                type="text"
                className="form-control"
                value={ value }
                name={ name }
                onChange={ this.handleChange }
                placeholder="UsuÃ¡rio"
              />
            </label>
          </div>
          <button
            data-testid="login-submit-button"
            type="submit"
            className="btn btn-primary"
            disabled={ disabled }
            onClick={ createUser({ name: value }) }
          >
            Entrar
          </button>
        </form>
      </div>
    );
  }
}

export default Login;
