import React from 'react';
import { Form } from 'reactstrap';
import Header from '../component/Header';
import searchAlbumsAPI from '../services/searchAlbumsAPI';

class Search extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      disabled: true,
      artist: '',
      /* loading: false, */
      /* list: [], */
    };

    this.handleChange = this.handleChange.bind(this);
    this.onClick = this.onClick.bind(this);
  }

  handleChange = ({ target }) => {
    const { value } = target;
    const nb = 2;
    if (value.length >= nb) {
      this.setState({
        name: value,
        disabled: false,
      });
    } else {
      this.setState({
        disabled: true,
      });
    }
  }

  async onClick() {
    const { name } = this.state;
    this.setState({
      disabled: false,
      artist: name,
      /* loading: true, */
    });
    const requisicao = await searchAlbumsAPI(name);
    console.log(requisicao);
    this.setState({
      name: '',
      /* loading: false, */
      /* list: requisicao, */
    });
  }

  render() {
    const { value, name, artist, disabled } = this.state;
    return (
      <div data-testid="page-search">
        <Header />
        <Form
          value={ value }
          name={ name }
          onChange={ this.onClick }
          disabled={ disabled }
          artist={ artist }
          handleChange={ this.handleChange }
        />
        {/* <Artista listAlbuns={ list } /> */}
      </div>
    );
  }
}

export default Search;
